import React, { useState } from 'react';
import { createStage, checkCollision } from '../gameHelpers';
import {StyledTetrisWrapper, StyledTetris} from './styles/StyledTetris';
import { usePlayer } from '../hooks/usePlayer';
import { useStage } from '../hooks/useStage';
import Stage from './Stage.js';
import Display from './Display.js';
import StartButton from './StartButton.js';

const Tetris = () => {
    const [dropTime, setDropTime] = useState(null); //speed based on level
    const [gameOver, setGameOver] = useState(false);

    const [player, updatePlayerPos, resetPlayer] = usePlayer(); 
    const [stage, setStage] = useStage(player); 

    const movePlayer = dir => { 

        if (!checkCollision(player, stage, { x: dir, y: 0}))
        updatePlayerPos({ x: dir, y: 0
        });
    }

    //Reset everything
    const startGame = () => {
        setStage(createStage());
        resetPlayer();
    }

    const drop = () => {
        updatePlayerPos({ 
            x: 0, 
            y: 1, 
            collided: false
        })
    }

    const dropPlayer = () => {
        drop();
    }

    const move = ({ keyCode }) => {
        if (!gameOver) {
            if (keyCode === 37) { //code for left arrow on keyboard  
                movePlayer(-1); //move left
            } else if (keyCode === 39) { //code for right arrow on keyboard
                movePlayer(1); //move right
            } else if (keyCode === 40) { //code for down arrow
                dropPlayer(); //move player down
            }
    }

    return (
        <StyledTetrisWrapper role="button" tabIndex="0" onKeyDown={ e => move(e)}>
            <StyledTetris>
                <Stage stage={stage}/>
                    <aside>
                        {gameOver ? (<Display gameOver={gameOver} text="Game Over!" />):(
                            <div>
                                <Display text="Score"/>
                                <Display text="Rows"/>
                                <Display text="Level"/>
                            </div>
                        )}
                        <StartButton callback={startGame()}/>
                    </aside>
            </StyledTetris>
        </StyledTetrisWrapper>
    )};
}
export default Tetris;
