export const STAGE_WIDTH = 12;
export const STAGE_HEIGHT = 20;

export const createStage = () => 
    Array.from(Array(STAGE_HEIGHT), () => 
        new Array(STAGE_WIDTH).fill([0, 'clear'])
    )


    //Use a for loop here, it'll be faster than a map

export const checkCollision = (player, stage, { x: moveX, y: moveY }) => {
    for (let y = 0; y < player.tetromino.length; y+= 1) {
        for (let x = 0; x < player.tetromino[y].length; x += 1) {

            //1. Check that we're on an actual Tetris block
            if (player.tetromino[y][x] !==0) {
                //2. Check that our movement is inside the game area's height, aka y
                //check if stage has y value in it
                !stage[y + player.pos.y + moveY] || 
                //3. Check that our movement is inside the game area's width, aka x
                ![stage]
                //4. Check that the cell we move to isn't set to clear
            }
        }
    }
}
