import React, { useState } from 'react';

import { createStage } from '../gameHelpers';

//Styled components
import {StyledTetrisWrapper, StyledTetris} from './styles/StyledTetris';

//Custom Hooks
import { usePlayer } from '../hooks/usePlayer';
import { useStage } from '../hooks/useStage';

//Components
import Stage from './Stage.js';
import Display from './Display.js';
import StartButton from './StartButton.js';

const Tetris = () => {

    //Creating 2 states -- drop time is the speed we modify based on llevel
    const [dropTime, setDropTime] = useState(null);
    const [gameOver, setGameOver] = useState(false);

    //Using our custom hooks here!

    const [player, updatePlayerPos, resetPlayer] = usePlayer(); //destructuring here
    const [stage, setStage] = useStage(player); //need our player here

    console.log('re-render');

    const movePlayer = dir => { //move left and right
        updatePlayerPos({ x: dir, y: 0});
    }

    const startGame = () => {//Reset everything
        setStage(createStage());
        resetPlayer();
    }

    const drop = () => {
        updatePlayerPos({ x: 0, y: 1, collided: false})
    }

    const dropPlayer = () => {
        drop();
    }

    const move = ({ keyCode }) => {
        if (!gameOver) {
            if (keyCode === 37) { //code for left arrow on keyboard  
                movePlayer(-1); //move left
            } else if (keyCode === 39) { //code for right arrow on keyboard
                movePlayer(1); //move right
            } else if (keyCode === 40) { //code for down arrow
                dropPlayer(); //move player down
            }
    }

    return (
        <StyledTetrisWrapper role="button" tabIndex="0" onKeyDown={ e=> move(e)}>
            <StyledTetris>
                <Stage stage={stage}/>
                    <aside>
                        {gameOver ? (<Display gameOver={gameOver} text="Game Over!" />) : (
                            <div>
                                <Display text="Score"/>
                                <Display text="Rows"/>
                                <Display text="Level"/>
                            </div>
                        )}

                        <StartButton onClick={startGame()}/>
                    </aside>
                </StyledTetris>
            </StyledTetrisWrapper>
    )
};
}
export default Tetris;
